-- Description: Seeds twenty shopping carts with representative items and optional discounts for testing.

BEGIN;

SET search_path TO shopping_cart, public;

ON CONFLICT (id) DO NOTHING;

ON CONFLICT (cart_id, product_id) DO NOTHING;

ON CONFLICT (cart_id, code) DO NOTHING;

COMMIT;

SELECT COUNT(*) AS cart_count FROM shopping_cart;
